# https://www.alitajran.com/unable-to-install-nuget-provider-for-powershell/#*************************************************************************************************#script that will update TLS and SSL# get current SLL and TLS version#The first cmdlet is to set strong cryptography on 64 bit .Net Framework (version 4 and above)#The second cmdlet is to set strong cryptography on 32 bit .Net Framework (version 4 and above).#install powershellget module# Show installed modules#find all Nuget versions#install Nuget version 2.8.5.208#*************************************************************************************************#CODEset-executionpolicy -ExecutionPolicy Unrestricted$PSVersionTable.PSVersion$PSVersionTable# get current SLL and TLS version[NET.ServicePointManager]::SecurityProtocol[Net.ServicePointManager]::SecurityProtocol = [Net.SecurityProtocolType]::Tls12#The first cmdlet is to set strong cryptography on 64 bit .Net Framework (version 4 and above)Set-ItemProperty -Path 'HKLM:\SOFTWARE\Wow6432Node\Microsoft\.NetFramework\v4.0.30319' -Name 'SchUseStrongCrypto' -Value '1' -Type DWord#The second cmdlet is to set strong cryptography on 32 bit .Net Framework (version 4 and above).Set-ItemProperty -Path 'HKLM:\SOFTWARE\Microsoft\.NetFramework\v4.0.30319' -Name 'SchUseStrongCrypto' -Value '1' -Type DWord#Windows 10 use #import-module Powershelget#install powershellget moduleInstall-Module PowershellGet -Force#installed modulesGet-installedmodule#find all versionsfind-packageprovider -Name "Nuget" -AllVersions#install Nuget version 2.8.5.208install-PackageProvider -Name "NuGet" -MinimumVersion 2.8.5.208 -Force#PowerShell Windows Update module installInstall-Module PSWindowsUpdate# install all Windows Updates and rebootInstall-WindowsUpdate -MicrosoftUpdate -AcceptAll -AutoReboot# beelow are commands that can give more information#Get-Command -module PSWindowsUpdate#Get-WUInstallerStatus#Get-WUHistory